import { useEffect, useState} from "react"
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import DeleteButton from "./DeleteButton";
import { useNavigate } from 'react-router-dom';
import './style.css'

const Students = () => {
    const [students, setStudents] = useState([]);
   
    const fetchData = async () => {
      try {
       const token = localStorage.getItem("token");
       console.log("Token:", token);
   
       const response = await fetch("http://localhost:8080/v1/student", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": token,
      },
    });
   
       if (!response.ok) {
         throw new Error("Error fetching data: " + response.statusText);
       }
   
       const data = await response.json();
       setStudents(data.data);
      } catch (error) {
        const navigate = useNavigate();

        console.error("Fetch error:", error);
        navigate("/");
    
        // Redirect to login page or handle unauthorized access
        // Example using React Router: history.push('/login');

      }
    };
   
    useEffect(() => {
       fetchData();
    }, []);

return (
    <div className="container">
        <div className="row">
        <h1 className="mb-3 mt-3 text-center">English Course Students</h1>
        {students.map((siswa)=>  {
            return (
                <div className="col-3">
                <CardStud id={siswa.stud_id} 
                name={siswa.stud_name}
                age={siswa.stud_age} 
                address={siswa.stud_address}
                phonenum={siswa.stud_phonenum} />
                </div>
            )
        })}
        </div>
       
    </div>
)
}



function CardStud(props){
    return (
        <Card style={{ width: '18rem' }}>
          <Card.Body>
            <Card.Title>{props.name}</Card.Title>
            <Card.Text>
             <ul>
                <li>Age: {props.age}</li>
                <li>Address: {props.address}</li>
                <li>Phone Number: {props.phonenum}</li>
             </ul>
            </Card.Text>
            <Button variant="primary">Edit</Button>
         <DeleteButton id={props.id} />
          </Card.Body>
        </Card>
      );
}

export default Students;
