import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Header from './Header';
import { useNavigate, useLocation, Link } from 'react-router-dom';


const add = async (name, age, address, phonenum) => {
    try {
        const token = localStorage.getItem("token");
        console.log("Token:", token);
      const response = await fetch("http://localhost:8080/v1/student", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": token,
        },
        body: JSON.stringify({
         stud_name: name,
         stud_age: parseInt(age),
         stud_address: address,
         stud_phonenum:phonenum,
        }),
      });
  
      if (response.ok) {
        // Login berhasil
        const data = await response.json();
        const token = data.token;
        // Simpan token di local storage
        localStorage.setItem("token", token);
      } else {
        // Login gagal
        alert("Data Gagal Ditambahkan");
      }
      return response;
    } catch (error) {
      console.error("Error during login:", error);
      throw error;
    }
  };
  
  
  const AddStudentForm = () => {
    const [name, setName] = useState("");
    const [age, setAge] = useState("");
    const [address, setAddress] = useState("");
    const [phonenum, setPhonenum] = useState("");
    const navigate = useNavigate();
    const location = useLocation();
    const { state } = location;
  
    const handleAdd = async () => {
      try {
        const response = await add(name,age,address,phonenum);
    
        if (response.ok) {
          // Add Succes
          navigate("/v1/student");
        } else {
          // fail
          alert("Data Gagal Ditambahkan");
        }
      } catch (error) {
        console.error("Error during login:", error);
      }
    };
  
    return (
        <div>
        <Header />
        <Form
          style={{
            width: '50%',
            margin: 'auto',
            marginTop: '50px',
            padding: '20px',
            border: '1px solid #ccc',
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          }}
          onSubmit={handleAdd}
        >
          <h3 className="text-center">Add Student Data</h3>
          <Form.Group className="mb-3" controlId="formName">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter name"
              name="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </Form.Group>
  
          <Form.Group className="mb-3" controlId="formAge">
            <Form.Label>Age</Form.Label>
            <Form.Control
              type="number"
              placeholder="Enter age"
              name="age"
              value={age}
              onChange={(e) => setAge(e.target.value)}
              required
            />
          </Form.Group>
  
          <Form.Group className="mb-3" controlId="formAddress">
            <Form.Label>Address</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter address"
              name="address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              required
            />
          </Form.Group>
  
          <Form.Group className="mb-3" controlId="formPhoneNumber">
            <Form.Label>Phone Number</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter phone number"
              name="phoneNumber"
              value={phonenum}
              onChange={(e) => setPhonenum(e.target.value)}
              required
            />
          </Form.Group>
  
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </div>
    );
  };

export default AddStudentForm
