// import React from 'react'
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import React, { useState } from 'react';


const Login = () => {
  
   // State untuk menyimpan token.
   const [token, setToken] = useState("");

   // Fungsi untuk mengirim permintaan ke endpoint `localhost:8080/login`.
   const login = async (username, password) => {
       const response = await axios.post("http://localhost:8080/login", {
           username,
           password
       });

       if (response.status === 200) {
           // Simpan token di dalam state.
           setToken(response.data.token);
       } else {
           // Tampilkan pesan kesalahan.
           alert("Username atau password salah");
       }
   };

  return (
    // <div className="container mt-5">
    //   <div className="d-flex justify-content-center">
    //     <Form className="col-md-6 shadow p-4 rounded">
    //       <h2 className="mb-4">Login</h2>
    //       <Form.Group controlId="formGroupEmail">
    //         <Form.Label>Username</Form.Label>
    //         <Form.Control type="text" placeholder="Enter Username" />
    //       </Form.Group>

    //       <Form.Group controlId="formGroupPassword" className="mb-4">
    //         <Form.Label>Password</Form.Label>
    //         <Form.Control type="password" placeholder="Password" />
    //       </Form.Group>

    //       <button onClick={() => login(username.value, password.value)}>Login</button>
    //     </Form>
    //   </div>
    // </div>
    <div>
    <input type="text" placeholder="Username" />
    <input type="password" placeholder="Password" />
    <button onClick={() => login(username.value, password.value)}>Login</button>
</div>
  )
}

export default Login
