// AddStudentForm.js
import React, { useState } from 'react';

const AddStudentForm = ({ onSubmit }) => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [address, setAddress] = useState('');
  const [phonenum, setPhoneNum] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validasi formulir di sini (jika diperlukan)

    // Panggil fungsi onSubmit dengan data mahasiswa baru
    onSubmit({ name, age, address, phonenum });

    // Bersihkan formulir setelah pengiriman
    setName('');
    setAge('');
    setAddress('');
    setPhoneNum('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </label>
      <br />
      <label>
        Age:
        <input type="number" value={age} onChange={(e) => setAge(e.target.value)} required />
      </label>
      <br />
      <label>
        Address:
        <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} required />
      </label>
      <br />
      <label>
        Phone Number:
        <input type="tel" value={phonenum} onChange={(e) => setPhoneNum(e.target.value)} required />
      </label>
      <br />
      <button type="submit">Add Student</button>
    </form>
  );
};

export default AddStudentForm;
