import { useEffect, useState} from "react"
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

const Students = ()=>{
const url = 'http://localhost:8080/v1/student'
const [students, setStudents] = useState([]);

const getDataStudents = async ()=> {
    const response = await fetch(url)
    const dataStudent = await response.json();
   setStudents(dataStudent.data);
   console.log(dataStudent.data);
}

useEffect(() =>{
    getDataStudents();
},[])

return (
    <div className="container">
        <div className="row">
        <h1 className="mb-3 mt-3 text-center">English Course Students</h1>
        {students.map((siswa)=>  {
            return (
                <div className="col-3">
                <CardStud id={siswa.stud_id} 
                name={siswa.stud_name}
                age={siswa.stud_age} 
                address={siswa.stud_address}
                phonenum={siswa.stud_phonenum} />
                </div>
            )
        })}
        </div>
       
    </div>
)
}

const DeleteButton = ({ id, onDelete }) => {
    const deleteUrl = `http://localhost:8080/v1/student/${id}`;
  
    const handleDelete = async () => {
      try {
        const response = await fetch(deleteUrl, {
          method: 'DELETE',
          headers: {
            'Content-Type': 'application/json',
            // Jika diperlukan, Anda bisa menyertakan header lain di sini
          },
        });
  
        if (response.ok) {
          // Jika penghapusan berhasil, panggil fungsi onDelete yang diberikan sebagai prop
          onDelete && onDelete();

          window.location.reload();
        } else {
          console.error('Gagal menghapus data:', response.status);
        }
      } catch (error) {
        console.error('Terjadi kesalahan:', error);
      }
    };
  
    return (
      <Button variant="danger" onClick={handleDelete}>
        Delete
      </Button>
    );
  };


function CardStud(props){
    return (
        <Card style={{ width: '18rem' }}>
          <Card.Body>
            <Card.Title>{props.name}</Card.Title>
            <Card.Text>
             <ul>
                <li>Age: {props.age}</li>
                <li>Address: {props.address}</li>
                <li>Phone Number: {props.phonenum}</li>
             </ul>
            </Card.Text>
            <Button variant="primary">Edit</Button>
         <DeleteButton id={props.id} onDelete={() => console.log('Data berhasil dihapus')} />
          </Card.Body>
        </Card>
      );
}

export default Students;
