import { useEffect, useState} from "react"
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import DeleteButton from "./DeleteButton";
import './style.css'

const Students = () => {
    const url = 'http://localhost:8080/v1/student';
    const [students, setStudents] = useState([]);
    const [token, setToken] = useState(""); // Tambahkan state untuk token
  
    useEffect(() => {
      const tokenFromLocalStorage = localStorage.getItem("token");
  
      // Pastikan token tersedia sebelum mengambil data
      if (tokenFromLocalStorage) {
        setToken(tokenFromLocalStorage);
      } else {
        // Jika tidak ada token, lakukan sesuatu (contohnya, mungkin arahkan ke halaman login)
        console.log("Token tidak tersedia. Lakukan penanganan sesuai kebutuhan.");
      }
    }, []);
  
    useEffect(() => {
      // Panggil fungsi getDataStudents hanya jika token sudah ada
      if (token) {
        getDataStudents();
      }
    }, [token]);
  
    const getDataStudents = async () => {
      try {
        const response = await fetch(url, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
  
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
  
        const dataStudent = await response.json();
        setStudents(dataStudent.data);
        console.log(dataStudent.data);
      } catch (error) {
        console.error('Error fetching students:', error);
      }
    };
  
    return (
      <div className="container">
        <div className="row">
          <h1 className="mb-3 mt-3 text-center">English Course Students</h1>
          {students.map((siswa) => {
            return (
              <div className="col-3" key={siswa.stud_id}>
                <CardStud
                  id={siswa.stud_id}
                  name={siswa.stud_name}
                  age={siswa.stud_age}
                  address={siswa.stud_address}
                  phonenum={siswa.stud_phonenum}
                />
              </div>
            );
          })}
        </div>
      </div>
    );
  };
  
  function CardStud(props) {
    return (
      <Card style={{ width: '18rem' }}>
        <Card.Body>
          <Card.Title>{props.name}</Card.Title>
          <Card.Text>
            <ul>
              <li>Age: {props.age}</li>
              <li>Address: {props.address}</li>
              <li>Phone Number: {props.phonenum}</li>
            </ul>
          </Card.Text>
          <Button variant="primary">Edit</Button>
          <DeleteButton id={props.id} />
        </Card.Body>
      </Card>
    );
  }

export default Students;
