import { useEffect, useState} from "react"
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import DeleteButton from "./DeleteButton";
import { useNavigate } from 'react-router-dom';
import './style.css'
import Header from "./Header";
import AddStudentForm from "./AddStudentForm";

const Students = () => {
    const [students, setStudents] = useState([]);
    const navigate = useNavigate();
   
    const fetchData = async () => {
      try {
       const token = localStorage.getItem("token");
       console.log("Token:", token);
   
       const response = await fetch("http://localhost:8080/v1/student", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": token,
      },
    });
   
       if (!response.ok) {
         throw new Error("Error fetching data: " + response.statusText);
       }
   
       const data = await response.json();
       setStudents(data.data);
      } catch (error) {

        console.error("Fetch error:", error);
        navigate("/",{ state: { error: 'Timeout Access. Please log in.' } });
    

      }
    };

    const handleAddStudent = async (newStudentData) => {
      try {
        const token = localStorage.getItem("token");
  
        // Kirim data mahasiswa baru ke API
        const response = await fetch("http://localhost:8080/v1/student", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": token,
          },
          body: JSON.stringify(newStudentData),
        });
  
        if (!response.ok) {
          throw new Error("Error adding student: " + response.statusText);
        }
  
        // Muat ulang data setelah menambahkan mahasiswa baru
        fetchData();
        setShowAddForm(false); // Sembunyikan formulir setelah penambahan berhasil
      } catch (error) {
        console.error("Add student error:", error);
        // Tampilkan pesan kesalahan jika diperlukan
      }
    };
   
    useEffect(() => {
       fetchData();
    }, []);

return (
  <div>
      <Header/>
    <div className="container mt-4">
    <Button variant="primary" onClick={() => setShowAddForm(true)}>
      Add Student</Button>

      {showAddForm && (
        <AddStudentForm onSubmit={handleAddStudent} />
        )}
        <div className="row mt-4">
        {students.map((siswa)=>  {
            return (
                <div className="col-3">
                <CardStud id={siswa.stud_id} 
                name={siswa.stud_name}
                age={siswa.stud_age} 
                address={siswa.stud_address}
                phonenum={siswa.stud_phonenum} />
                </div>
            )
        })}
        </div>
       
    </div>
    </div>
)
}



function CardStud(props){
    return (
        <Card style={{ width: '18rem' }}>
          <Card.Body>
            <Card.Title>{props.name}</Card.Title>
            <Card.Text>
             <ul>
                <li>Age: {props.age}</li>
                <li>Address: {props.address}</li>
                <li>Phone Number: {props.phonenum}</li>
             </ul>
            </Card.Text>
            <Button variant="primary">Edit</Button>
         <DeleteButton id={props.id} />
          </Card.Body>
        </Card>
      );
}

export default Students;
