import { useEffect, useState} from "react"
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import DeleteButton from "./DeleteButton";
import { useNavigate } from 'react-router-dom';
import './style.css'
import Header from "./Header";

const Students = () => {
    const [students, setStudents] = useState([]);
    const navigate = useNavigate();
   

    const fetchData = async () => {
      try {
       const token = localStorage.getItem("token");
       console.log("Token:", token);
   
       const response = await fetch("http://localhost:8080/v1/student", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": token,
      },
    });
   
       if (!response.ok) {
        if (!token) {
          // Redirect ke halaman login jika token tidak ada
          navigate('/', { state: { error: 'Silakan login untuk mengakses halaman ini' } });
        }else if (response.status === 401) {
          // Unauthorized, token tidak valid
             const token = localStorage.getItem("token");
            navigate('/', { state: { error: 'Timeout Access. Please log in' } });
         
        } else {
          // Gagal
          const errorData = await response.json();
          throw new Error(errorData.message);
        }
       }
   
       const data = await response.json();
       setStudents(data.data);
      } catch (error) {

        console.error("Fetch error:", error);
      }
    };

    
   
    useEffect(() => {
       fetchData();
    }, []);

    const handleAddStudentClick = () => {
      // Navigasi ke halaman form
      navigate("/v1/student/add");
  };
return (
  <div>
      <Header/>
    <div className="container mt-4">
    <Button variant="primary" navigate={navigate} onClick={handleAddStudentClick} >Add Student</Button>
        <div className="row mt-4">
        {students.map((siswa)=>  {
            return (
                <div className="col-3">
                <CardStud id={siswa.stud_id} 
                name={siswa.stud_name}
                age={siswa.stud_age} 
                address={siswa.stud_address}
                phonenum={siswa.stud_phonenum} />
                </div>
            )
        })}
        </div>
       
    </div>
    </div>
)
}



function CardStud(props){
    return (
        <Card className="mb-4" style={{ width: '18rem' }}>
          <Card.Body>
            <Card.Title>{props.name}</Card.Title>
            <Card.Text>
             <ul>
                <li>Age: {props.age}</li>
                <li>Address: {props.address}</li>
                <li>Phone Number: {props.phonenum}</li>
             </ul>
            </Card.Text>
            <Button variant="primary">Edit</Button>
         <DeleteButton id={props.id} />
          </Card.Body>
        </Card>
      );
}

export default Students;
