import { useEffect, useState} from "react"
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import DeleteButton from "./DeleteButton";
import './style.css'


const Students = ()=>{
    const [students, setStudents] = useState([]);
    const token = localStorage.getItem("token");
  
    useEffect(() => {
      // Tambahkan token ke header Authorization
      const headers = new Headers();
      headers.append("Authorization", `Bearer ${token}`);
  
      fetch("http://localhost:8080/students", {
        method: "GET",
        headers: headers,
      })
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        // Setel data mahasiswa ke state
        setStudents(data);
      })
      .catch(error => {
        console.error('Error fetching students:', error);
      });
    }, [token]);

return (
    <div className="container">
        <div className="row">
        <h1 className="mb-3 mt-3 text-center">English Course Students</h1>
        {students.map((siswa)=>  {
            return (
                <div className="col-3">
                <CardStud id={siswa.stud_id} 
                name={siswa.stud_name}
                age={siswa.stud_age} 
                address={siswa.stud_address}
                phonenum={siswa.stud_phonenum} />
                </div>
            )
        })}
        </div>
       
    </div>
)
}



function CardStud(props){
    return (
        <Card style={{ width: '18rem' }}>
          <Card.Body>
            <Card.Title>{props.name}</Card.Title>
            <Card.Text>
             <ul>
                <li>Age: {props.age}</li>
                <li>Address: {props.address}</li>
                <li>Phone Number: {props.phonenum}</li>
             </ul>
            </Card.Text>
            <Button variant="primary">Edit</Button>
         <DeleteButton id={props.id} />
          </Card.Body>
        </Card>
      );
}

export default Students;
