import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Header from './Header';
import { useNavigate, useLocation, Link } from 'react-router-dom';

const add = async (name, age, address, phonenum) => {
    try {
        const token = localStorage.getItem("token");
        console.log("Token:", token);
      const response = await fetch("http://localhost:8080/v1/student", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": token,
        },
        body: JSON.stringify({
         name,
         age,
         address,
         phonenum,
        }),
      });
  

      return response;
    } catch (error) {
      console.error("Error during login:", error);
      throw error;
    }
  };
  
  
  const AddStudentForm = () => {
    const [name, setName] = useState("");
    const [age, setAge] = useState("");
    const [address, setAddress] = useState("");
    const [phonenum, setPhonenum] = useState("");
    const navigate = useNavigate();
    const location = useLocation();
    const { state } = location;
  
    const handleAdd = async () => {
      try {
        const response = await add(name,age,address,phonenum);
    
        if (response.ok) {
          // Add Succes
          navigate("/v1/student");
        } else {
          // fail
          alert("Data Gagal Ditambahkan");
        }
      } catch (error) {
        console.error("Error during login:", error);
      }
    };
  
    return (
      <div>
        <Header />
        <div className="login-container">
      <h2 className='text-center'>Add Data Student</h2>
    <input
      type="text"
      placeholder="Name"
      value={name}
      onChange={(e) => setName(e.target.value)}
      className="input-field"
    />
    <input
      type="number"
      placeholder="Age"
      value={age}
      onChange={(e) => setAge(e.target.value)}
      className="input-field"
    />
    <button onClick={handleLogin} className="login-button">
      Login
    </button>
    {state && state.error && (
        <p className='text-center mt-4' style={{ color: "red" }}>{state.error}</p>
      )}
  </div>
      </div>
    );
  };

export default AddStudentForm
